table(lit$YEAR) |&gt;
  as.data.frame() |&gt;
  echarts4r::e_charts(Var1) |&gt;
  echarts4r::e_bar(Freq, name = "Articles") |&gt;
  echarts4r::e_title("Number of articles per year")








